import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import {
  Container,
  Paper,
  Typography,
  TextField,
  Button,
  Box,
  Alert,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Fade,
  CircularProgress,
  FormHelperText
} from '@mui/material';
import { PersonAdd } from '@mui/icons-material';
import { register, clearErrors } from '../../store/slices/authSlice';

const validationSchema = Yup.object({
  name: Yup.string()
    .required('Name is required')
    .min(2, 'Name must be at least 2 characters'),
  email: Yup.string()
    .email('Invalid email address')
    .required('Email is required'),
  password: Yup.string()
    .required('Password is required')
    .min(6, 'Password must be at least 6 characters'),
  role: Yup.string()
    .oneOf(['creator', 'consumer'], 'Invalid role')
    .required('Role is required')
});

const Register = () => {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const { loading, errors, isAuthenticated } = useSelector(state => state.auth);

  useEffect(() => {
    if (isAuthenticated) {
      navigate('/');
    }
    return () => {
      dispatch(clearErrors());
    };
  }, [isAuthenticated, navigate, dispatch]);

  // Helper function to get field error
  const getFieldError = (fieldName) => {
    if (!errors) return null;
    const error = errors.find(err => err.field === fieldName);
    return error ? error.message : null;
  };

  const formik = useFormik({
    initialValues: {
      name: '',
      email: '',
      password: '',
      role: 'consumer'
    },
    validationSchema,
    onSubmit: (values) => {
      dispatch(register(values));
    }
  });

  // Get general error (non-field specific)
  const generalError = getFieldError('general');

  return (
    <Container maxWidth="sm">
      <Fade in={true} timeout={500}>
        <Box 
          sx={{ 
            mt: 8,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
          }}
        >
          <Paper 
            elevation={3} 
            sx={{ 
              p: 4,
              width: '100%',
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
              gap: 3,
            }}
          >
            <Typography 
              variant="h4" 
              component="h1" 
              gutterBottom 
              align="center"
              sx={{
                color: 'primary.main',
                fontWeight: 600,
              }}
            >
              Create Account
            </Typography>

            {generalError && (
              <Alert 
                severity="error" 
                sx={{ 
                  width: '100%',
                  borderRadius: 2,
                }}
              >
                {generalError}
              </Alert>
            )}

            <form 
              onSubmit={formik.handleSubmit}
              style={{ width: '100%' }}
            >
              <TextField
                fullWidth
                id="name"
                name="name"
                label="Name"
                value={formik.values.name}
                onChange={formik.handleChange}
                error={Boolean(formik.touched.name && (formik.errors.name || getFieldError('name')))}
                helperText={
                  formik.touched.name && (formik.errors.name || getFieldError('name'))
                }
                margin="normal"
                sx={{
                  '& .MuiOutlinedInput-root': {
                    '&:hover fieldset': {
                      borderColor: 'primary.main',
                    },
                  },
                }}
              />

              <TextField
                fullWidth
                id="email"
                name="email"
                label="Email"
                value={formik.values.email}
                onChange={formik.handleChange}
                error={Boolean(formik.touched.email && (formik.errors.email || getFieldError('email')))}
                helperText={
                  formik.touched.email && (formik.errors.email || getFieldError('email'))
                }
                margin="normal"
                sx={{
                  '& .MuiOutlinedInput-root': {
                    '&:hover fieldset': {
                      borderColor: 'primary.main',
                    },
                  },
                }}
              />

              <TextField
                fullWidth
                id="password"
                name="password"
                label="Password"
                type="password"
                value={formik.values.password}
                onChange={formik.handleChange}
                error={Boolean(formik.touched.password && (formik.errors.password || getFieldError('password')))}
                helperText={
                  formik.touched.password && (formik.errors.password || getFieldError('password'))
                }
                margin="normal"
                sx={{
                  '& .MuiOutlinedInput-root': {
                    '&:hover fieldset': {
                      borderColor: 'primary.main',
                    },
                  },
                }}
              />

              <FormControl 
                fullWidth 
                margin="normal"
                error={Boolean(formik.touched.role && (formik.errors.role || getFieldError('role')))}
                sx={{
                  '& .MuiOutlinedInput-root': {
                    '&:hover fieldset': {
                      borderColor: 'primary.main',
                    },
                  },
                }}
              >
                <InputLabel id="role-label">Role</InputLabel>
                <Select
                  labelId="role-label"
                  id="role"
                  name="role"
                  value={formik.values.role}
                  onChange={formik.handleChange}
                  label="Role"
                >
                  <MenuItem value="consumer">Consumer</MenuItem>
                  <MenuItem value="creator">Creator</MenuItem>
                </Select>
                {formik.touched.role && (formik.errors.role || getFieldError('role')) && (
                  <FormHelperText error>
                    {formik.errors.role || getFieldError('role')}
                  </FormHelperText>
                )}
              </FormControl>

              <Button
                type="submit"
                fullWidth
                variant="contained"
                size="large"
                disabled={loading}
                sx={{ 
                  mt: 3,
                  mb: 2,
                  height: 48,
                  backgroundColor: 'primary.main',
                  '&:hover': {
                    backgroundColor: 'primary.dark',
                  },
                }}
              >
                {loading ? (
                  <CircularProgress size={24} color="inherit" />
                ) : (
                  'Create Account'
                )}
              </Button>
            </form>
          </Paper>
        </Box>
      </Fade>
    </Container>
  );
};

export default Register;
